import axios from "axios";
import { useEffect, useState } from "react";

export function AddTicket() {

    let [hardwares, setHardwares] = useState([]);
    let [technicians, setTechnicians] = useState([]);
    let [warehouses, setWarehouses] = useState([]);

    async function getHardwares() {
        try {
            let response = await axios.get("http://localhost:3001/hardwares");
            setHardwares(response.data);
        }
        catch (error) { alert(`Error ${error}`); }
    }
    async function getTechnicians() {
        try {
            let response = await axios.get("http://localhost:3001/technicians");
            setTechnicians(response.data);
        }
        catch (error) { alert(`Error ${error}`); }
    }

    async function getWarehouses() {
        try {
            let response = await axios.get("http://localhost:3001/warehouses");
            setWarehouses(response.data);
        }
        catch (error) { alert(`Error ${error}`); }
    }

    useEffect(() => {
        getHardwares();
        getTechnicians();
        getWarehouses();
    }, []);
    

    let [title, setTitle] = useState('');
    let [description, setDescription] = useState('');
    let [customer, setCustomer] = useState('');
    let [hardware, setHardware] = useState('');
    let [technician, setTechnician] = useState('');
    let [warehouse, setWarehouse] = useState('');
    let [date, setDate] = useState('');

    async function addTicket() {
        try {
            let ticket = { title, description, customer, hardware, technician, warehouse, date };
            let response = await axios.post("http://localhost:3001/tickets", ticket);
            alert("Ticket added");
        }
        catch (error) { alert(`Ticket was not added ${error}`); }
    }


    return (
        <div className="add-ticket-div">
            <h1>Add Ticket</h1>
            <form className="add-ticket-form">
                <label htmlFor="title">Title</label>
                <input type="text" id="title" value={title} onChange={e => setTitle(e.target.value)} />
                <label htmlFor="description">Description</label>
                <input type="text" id="description" value={description} onChange={e => setDescription(e.target.value)} />
                <label htmlFor="customer">Customer</label>
                <input type="text" id="customer" value={customer} onChange={e => setCustomer(e.target.value)} />
                <label htmlFor="hardware">Hardware</label>
                <input type="text" id="hardware" value={hardware} onChange={e => setHardware(e.target.value)} />
                <label htmlFor="technician">Technician</label>
                <input type="text" id="technician" value={technician} onChange={e => setTechnician(e.target.value)} />
                <label htmlFor="warehouse">Warehouse</label>
                <input type="text" id="warehouse" value={warehouse} onChange={e => setWarehouse(e.target.value)} />
                <label htmlFor="date">Date</label>
                <input type="date" id="date" value={date} onChange={e => setDate(e.target.value)} />
                <button type="button" onClick={addTicket}>Add Ticket</button>
            </form>


        </div>
    );
}
